package Interview;

public class SumOfNaturalNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		int num=1;
		for(i=0;i<=10;i++)
		{
			num=num+i;
		}
		System.out.println(num);
		
		int number=1;
		for(int j=0;j<=10;j++)
		{
			number=number+j;
		}
		System.out.println(number);
		
		
	}

}
